CREATE DEFINER=`root`@`localhost` PROCEDURE `DescriptionCounterOfSpells` ()  NO SQL
BEGIN
	SELECT CHAR_LENGTH(S.Description),S.Name,S.Description as Descrizione FROM spells S WHERE CHAR_LENGTH(S.Description) > 1024 ORDER BY CHAR_LENGTH(S.Description) DESC;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `getSpellsByClass` (IN `class` VARCHAR(45))  BEGIN
	SELECT 	I.Name,
			I.Type,
            I.Level,
            I.CastingTime,
            I.Components,
            I.Duration,
            I.RangeSpell,
			I.Description,
            group_concat(CI.className) as Classe
    FROM spells I INNER JOIN classxspells CI ON I.Name = CI.spellName
    WHERE CI.className = class
    GROUP BY I.Name
    ORDER BY I.Level, I.Name;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `getSpellsByClassLevel` (IN `classe` VARCHAR(45), IN `livello` INT)  BEGIN
	SELECT 	I.Name,
			I.Type,
            I.Level,
            I.CastingTime,
            I.Components,
            I.Duration,
            I.RangeSpell,
			I.Description,
            CI.className
    FROM spells I INNER JOIN classxspells CI ON I.Name = CI.spellName 
    WHERE CI.className = classe and I.Level = livello
    ORDER BY I.Level,I.Name;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `getSpellsByLevel` (IN `level` INT)  BEGIN
	SELECT 	S.Name,
			S.Type,
            S.Level,
            S.CastingTime,
            S.Components,
            S.Duration,
            S.RangeSpell,
			S.Description,
            group_concat(S.Name) as Incantesimi
FROM spells S WHERE S.Level=level
    GROUP BY S.Name
    ORDER BY S.Name;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `getSpellsByName` (IN `nameinput` VARCHAR(100))  BEGIN
	SELECT 	I.Name,
			I.Type,
            I.Level,
            I.CastingTime,
            I.Components,
            I.Duration,
            I.RangeSpell,
			I.Description,
            group_concat(CI.className) as Class
    FROM spells I INNER JOIN classxspells CI ON I.Name = CI.spellName
    WHERE I.Name like CONCAT('%', nameinput , '%')
    GROUP BY I.Name;
END$$

DELIMITER ;

